#ifndef BCPUINSTPRINTER_H
#define BCPUINSTPRINTER_H

#include "llvm/MC/MCInstPrinter.h"

namespace llvm {

struct BCpuInstPrinter : public MCInstPrinter {
  BCpuInstPrinter(const MCAsmInfo &MAI, const MCInstrInfo &MII,
                  const MCRegisterInfo &MRI)
      : MCInstPrinter(MAI, MII, MRI) {}

  // Autogenerated by tblgen.
  std::pair<const char *, uint64_t> getMnemonic(const MCInst *MI) override;
  void printInstruction(const MCInst *MI, uint64_t Address, raw_ostream &O);
  static const char *getRegisterName(MCRegister Reg);

  void printRegName(raw_ostream &OS, MCRegister Reg) const override;
  void printInst(const MCInst *MI, uint64_t Address, StringRef Annot,
                 const MCSubtargetInfo &STI, raw_ostream &O) override;

  void printOperand(const MCInst *MI, int OpNo, raw_ostream &OS);
};

} // end namespace llvm

#endif // BCPUINSTPRINTER_H
